import React, { useState } from "react";
import axios from "axios";

interface LocationButtonProps {
  onCitySelect: (lat: string, lng: string, loc: string) => void;
}

const LocationButton: React.FC<LocationButtonProps> = ({ onCitySelect }) => {
  const handleGetLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const { latitude, longitude } = position.coords;
          try {
            const fetchedPlaceName = await fetchPlaceName(latitude, longitude);
            if (fetchedPlaceName) {
              onCitySelect(
                latitude.toString(),
                longitude.toString(),
                fetchedPlaceName
              );
            } else {
              console.error("Location not found");
            }
          } catch (error) {
            console.error("Error getting location:", error);
          }
        },
        (error) => {
          console.error("Error getting location:", error);
        }
      );
    } else {
      console.error("Geolocation not supported by this browser.");
    }
  };

  const fetchPlaceName = async (
    lat: number,
    lng: number
  ): Promise<string | null> => {
    try {
      const response = await axios.get(
        `https://api.mapbox.com/geocoding/v5/mapbox.places/${lng},${lat}.json`,
        {
          params: {
            access_token: process.env.REACT_APP_MAPBOX_ACCESS_TOKEN,
            limit: 1,
          },
        }
      );

      const placeName = response.data.features[0]?.place_name;
      return placeName || null;
    } catch (error) {
      console.error("Error while fetching place name:", error);
      return null;
    }
  };

  return (
    <div>
      <button onClick={handleGetLocation}>Get Current Location</button>
    </div>
  );
};

export default LocationButton;
