import React, { useState, useEffect } from "react";
import Select from "react-select";
import axios from "axios";

// Define types for city data
interface CityOption {
  label: string;
  value: { city: string; state: string; country: string };
}

interface SearchBarProps {
  onCitySelect: (lat: string, lng: string) => void;
}

const SearchBar: React.FC<SearchBarProps> = ({ onCitySelect }) => {
  const [options, setOptions] = useState<CityOption[]>([]);
  const [selectedCity, setSelectedCity] = useState<CityOption | null>(null);

  const fetchCities = async (input: string) => {
    if (input.length < 2) return;

    try {
      const response = await axios.get(
        `https://nominatim.openstreetmap.org/search`,
        {
          params: {
            q: input,
            format: "json",
            addressdetails: 1,
          },
          headers: {
            "User-Agent":
              "Weather-Forecasting-App/1.0 (yche0323@student.monash.edu)",
          },
        }
      );
      const cities = response.data.map((result: any) => ({
        label: `${result.address.city || ""}, ${result.address.state || ""}, ${
          result.address.country || ""
        }`,
        value: {
          city: result.address.city || "",
          state: result.address.state || "",
          country: result.address.country || "",
          lat: result.lat,
          lon: result.lon,
        },
      }));
      setOptions(cities);
    } catch (error) {
      console.error("Error fetching citites:", error);
    }
  };

  const handleChange = (selectedOption: CityOption | null) => {
    setSelectedCity(selectedOption);
    if (selectedOption) {
      const { city, state, country } = selectedOption.value;
      console.log(`Selected city: ${city}, ${state}, ${country}`);
      fetchCoordinates(city, state, country);
    }
  };

  const fetchCoordinates = async (
    city: string,
    state: string,
    country: string
  ) => {
    try {
      const response = await axios.get(
        `https://nominatim.openstreetmap.org/search`,
        {
          params: {
            q: `${city}, ${state}, ${country}`,
            format: "json",
            addressdetails: 1,
          },
        }
      );
      const { lat, lng } = response.data[0];
      console.log(`Coordinates: Latitude ${lat}, Longitude ${lng}`);
      onCitySelect(lat.toString(), lng.toString());
    } catch (error) {
      console.error("Error fetching coordinates:", error);
    }
  };

  return (
    <Select
      options={options}
      onInputChange={fetchCities}
      onChange={handleChange}
      placeholder="Enter a city"
    />
  );
};

export default SearchBar;
