import React, { useState, useEffect } from "react";
import Select from "react-select";
import axios from "axios";

// Define types for city data
interface CityOption {
  label: string;
  value: { city: string; state: string; country: string };
}

interface SearchBarProps {
  onCitySelect: (lat: string, lng: string) => void;
}

const SearchBar: React.FC<SearchBarProps> = ({ onCitySelect }) => {
  const [options, setOptions] = useState<CityOption[]>([]);
  const [selectedCity, setSelectedCity] = useState<CityOption | null>(null);

  const fetchCities = async (input: string) => {
    if (input.length < 2) return;

    try {
      const response = await axios.get(
        `https://api.mapbox.com/geocoding/v5/mapbox.places/${encodeURIComponent(
          input
        )}.json`,
        {
          params: {
            access_token: process.env.MAPBOX_ACCESS_TOKEN,
            limit: 5,
            types: "place",
          },
        }
      );

      const cities = response.data.features.map((feature: any) => ({
        lable: feature.place_name,
        value: {
          city: feature.text, // Extract city name
          state:
            feature.context.find((c: any) => c.id.includes("region"))?.text ||
            "", // Extract state
        },
      }));

      setOptions(cities);
    } catch (error) {
      console.error("Error fetching citites:", error);
    }
  };

  const handleChange = (selectedOption: CityOption | null) => {
    setSelectedCity(selectedOption);
    if (selectedOption) {
      const { city, state, country } = selectedOption.value;
      console.log(`Selected city: ${city}, ${state}, ${country}`);
      fetchCoordinates(city, state, country);
    }
  };

  const fetchCoordinates = async (
    city: string,
    state: string,
    country: string
  ) => {
    try {
      const response = await axios.get(
        `https://nominatim.openstreetmap.org/search`,
        {
          params: {
            q: `${city}, ${state}, ${country}`,
            format: "json",
            addressdetails: 1,
          },
        }
      );
      const { lat, lon } = response.data[0];
      console.log(`Coordinates: Latitude ${lat}, Longitude ${lon}`);
      onCitySelect(lat.toString(), lon.toString());
    } catch (error) {
      console.error("Error fetching coordinates:", error);
    }
  };

  return (
    <Select
      options={options}
      onInputChange={(inputValue) => {
        fetchCities(inputValue)
          .then(() => console.log("Cities updates"))
          .catch((error) => console.log("Error updating cities:", error));
      }}
      onChange={handleChange}
      placeholder="Enter a city"
    />
  );
};

export default SearchBar;
